          <div className="centered">
              <img src="logo.png" alt="Avatar woman"></img>   
          </div>
          <div id="textInLeft">Hear what people are talking about.</div>
        </div>
        <div className="split right">

          <img src={require('./logo.gif')}  />
          <h1>See whatâ€™s happening in the Instazen right now</h1>
          <button id="btnSignIn" type="button" >Sign In</button>
          <br></br><br></br>
          <div>
           {/* {this.renderRedirect()}  */}
            <button id="btnLogIn" type="button" onClick={this.handleOnClick.bind(this)} >Log In</button>

          </div>

        </div>


    </div>
    )
  }
}

export default App
