import React, { Component } from 'react'
import Menu from './Menu'
import './Followers.css'
import history from './../history';
import axios from 'axios'
import { Link } from 'react-router-dom';
import {allUsersArray} from './Menu'
import {followingUserArray} from './Followings'

export class Followers extends Component {

constructor(props) {
    super(props)

    this.state = {

        token:localStorage.getItem('token'),
         followers:[],
         followingUserName:'',
         followerUserName:'',
         person:'',
         followingPerson:''
    }

}

viewProfile=(otherUserName,event)=>{

    console.log(otherUserName)
    localStorage.setItem('otheUserName',otherUserName)
    history.push('/OtherUserProfile')
}


// onclick function to follow the user
followUserBtn=(user,event)=>{

    const{followingUserName,person}=this.state

    console.log(user)
    const followuser={
        token:this.state.token,
        otheruser:user

    }

    axios
    .post('http://127.0.0.1:8000/User/Followers/Follow/',followuser)
    .then(response=>{
        console.log(response)    // will return 200 code if token is null
        this.setState({
            followingUserName:user
        })
        console.log('other user- ',followingUserName)
        const followPerson=followingUserArray.find(perso=> perso == user);
        const personName=allUsersArray.find(perso=> perso == user);
        this.setState({
            person:personName,
            followingPerson:followPerson
        })
        console.log('all ',allUsersArray)
        if(response['status']==200){
            console.log('Followed Successfully!')
            history.push('/Followers')

        }
        else if(response['status']==208){

            console.log('User unfollowed!!')
            history.push('/Followers')

        }

    })
    .catch(error=>{
        console.log(error.response)
    })



}

// onclick function to unfollow the user
unfollowUserBtn=(user,event)=>{

    console.log(user)
    const unfollowuser={
        token:this.state.token,
        otheruser:user

    }

    axios
    .post('http://127.0.0.1:8000/User/Followers/Unfollow/',unfollowuser)
    .then(response=>{
        console.log(response)
        this.setState({
            followingUserName:user
        })
        // console.log('other user- ',followingUserName)
        const follow_Person=followingUserArray.find(perso=> perso == user);
        const personName=allUsersArray.find(perso=> perso == user);
        this.setState({
            person:personName,
            followingPerson:follow_Person
        })


        if(response['status']==200){
           console.log('UnFollowed Successfully!')
            history.push('/HomePage')

        }
        else if(response['status']==208){
            console.log('User already followed!!')
            history.push('/HomePage')

        }

    })
    .catch(error=>{
        if(error.response['status']==406){
            console.log('already unfollowed')
            history.push('/Followings')
        }
        else if(error.response['status']==400){
            console.log('unfollowing yourself')
            history.push('/UserProfile')
        }
    })



}

checker=(follow)=>{

    const{followers}=this.state

    // for(var i=0;i<followers.length;i++){

        var temp=allUsersArray.find(username=> username==follow)
        var temp2=followingUserArray.find(user=> user!=follow)
        this.setState({
            person:temp,
            followingPerson:temp2
        })
    // }

}



componentDidMount(){
    if(this.state.followers[0]==''){

        history.push('./HomePage') 
        alert('No Any Followers!')
    }
    else{

        axios
        .post('http://127.0.0.1:8000/User/Followers/',this.state)
        .then(response=>{
            console.log(response)
            const followingsArray=response.data

            if(response['status']==200){

                this.setState({

                    followers:followingsArray
                })
                console.log(allUsersArray)

                for(var i=0;i<followingsArray.length;i++){
                    this.checker(followingsArray[i])
                }



            }
        })
        .catch(error=>{
            // console.log(error.response['status']);
            // if(error.response['status']==504){
            //     history.push('/')
            // }
        })

    }
}

    render() {
        const{followingUserName,person,followingPerson}=this.state

        return (
            <div>
                <Menu />
                <div className="UserFollowers">
                <div id="label"> Your Followers </div>
                    <div id="followers-list"> 
                    {this.state.followers.map(follow => (
                                <div key={indexedDB} onLoadStart={this.checker(follow)}>
                                    {/* <div id="followers-name">{followers.username}<br/></div> */}

                                <div id="followers-username">
                                    <Link onClick={()=>{
                                        this.viewProfile(follow)
                                    }}>@{follow}</Link>
                                    {/* <div onLoad={this.checker(follow)}></div> */}
                                    {
                                        person == follow  && followingPerson !=follow?
                                        <div id="view-profile-btn">
                                        <button type="button"
                                        onClick={()=>{this.followUserBtn(follow)}}>
                                        follow
                                    </button></div>: 
                                    <div id="view-profile-btn">
                                        <button type="button"
                                        onClick={()=>{this.unfollowUserBtn(follow)}}>
                                    unfollow
                                    </button></div>


                                    }

                                </div>
                                </div>
                            ))}
                    </div>
                </div>
            </div>
        )
    }
}


export default Followers
