import React, { Component } from 'react'
import './RegistrationPage.css'
import history from './history';

export class RegistrationPage extends Component {
    constructor(props) {
        super(props)

        this.state = {
             username:'',
             password:'',
             email:'',
             phone:'',
             firstname:'',
             lastname:'',
             dob:'',
             userAccount:[]
        }
    }
    clickEvent=event=>{
        alert(`Hey ${this.state.firstname}.... Registration Successful! `);
        this.state.userAccount.push(this.state);
        console.log(this.state.userAccount);
        history.push('/LoginPage')
        event.preventDefault()
    }

    onChangeEvent=event=>{
        this.setState({
            [event.target.name]:event.target.value
        });


    }


    render() {
        const{username,password,email,phone,dob,firstname,lastname }=this.state
        return (
            <div className='reg'> 
            <h1 id="title">InstaTwitter</h1>
                <img src="./logo.png"></img>
            <div className="box">
            <form>

                <div className="registrationPage"><br></br>
                    <div id="textRegister">Registration</div>
                    <h3>Create your account here!</h3>
                    <br></br><br></br>
                    <label>Name</label>
                    <input
                        type="text" 
                        id="firstname" 
                        name="firstname"
                        value={firstname}
                        onChange={this.onChangeEvent}>

                    </input>
                    {/* <br></br>
                    <br></br> */}

                    <input type="text" 
                        id="lastname" 
                        placeholder="Enter Last Name"
                        name="lastname"
                        value={lastname}
                        onChange={this.onChangeEvent}>
                    </input>
                    {/* <br></br>
                    <br></br> */}

                    <input type="email" 
                        id="email" 
                        placeholder="Enter Email ID "
                        name="email"
                        value={email}
                        onChange={this.onChangeEvent}>
                    </input>
                    {/* <br></br>
                    <br></br> */}

                    <input type="text" 
                        id="phone" 
                        placeholder="Enter Phone Number "
                        name="phone"
                        value={phone}
                        onChange={this.onChangeEvent}>
                    </input>
                    {/* <br></br>
                    <br></br> */}

                    <input type="text" 
                        id="use-rname" 
                        placeholder="Enter username "
                        name="username"
                        value={username}
                        onChange={this.onChangeEvent}>
                    </input>
                    {/* <br></br>
                    <br></br> */}

                    <input type="password" 
                        id="pass-word" 
                        placeholder="Set Password" 
                        name="password"
                        value={password}
                        onChange={this.onChangeEvent}>
                    </input>
                    {/* <br></br>
                    <br></br> */}

                    <input type="password" 
                        id="confirm-password" 
                        placeholder="Confirm Password" 
                        name="password"
                        value={password}
                        onChange={this.onChangeEvent}>
                    </input>

                    <br></br><br></br>

                    <button id="btnSignIn" type="Submit" onClick={this.clickEvent} >Register</button>


                 </div>

                </form>
                </div>  
            </div>
        )
    }
}

export default RegistrationPage
