import React, { Component } from 'react'
import './LoginPage.css';

class LoginPage extends Component {
    constructor(props) {
        super(props)

        this.state = {
             userName:'',
             passWord:'',
             userDetails:[],


        }
    }

    handleEvent=event=>{
        event.preventDefault()
        if(validateForm(this.state.errors)) {
            console.info('Valid Form')
            this.state.userDetails.push(this.state);
            alert(`Welcome ${this.state.userName} `)
            console.log(this.state);
            console.log(this.state.userDetails)
          }
          else{
            console.error('Invalid Form')
            alert(`Invalid Details `)
          }



    }
    changeEventUserName=event=>{
        this.setState({
           userName:event.target.value
        })
    }
    changeEventPassWord=event=>{
            this.setState({
              passWord:event.target.value
            })

    }

    render() {

        const { userName, passWord } = this.state
        return (
        <div className="loginPage">
            <form>
                {/* <h1 id="title">InstaTwitter</h1> */}
                <img src={require('./logo.gif')}  />
                <div id="textLogin">Login to InstaTwitter</div><br></br><br></br>
                <input 
                    type="text" 
                    id="username" 
                    placeholder="Enter username or phone number"
                    value={userName}
                    name={userName}
                    required=""
                    onChange={this.changeEventUserName}>
                </input><br></br>
                <br></br>
                <input 
                    type="password" 
                    id="password" 
                    placeholder="Enter Password" 
                    value={passWord}
                    name={passWord}
                    required=""
                    onChange={this.changeEventPassWord}>
                </input><br></br>
                <br></br><br></br>
                <button id="btnLogIn" type="button" onClick={this.handleEvent} >Log In</button>
            </form>   

        </div>
        )
    }
}

export default LoginPage
