import React, { Component } from 'react'
import './LoginPage.css';

class LoginPage extends Component {
    constructor(props) {
        super(props)

        this.state = {
             userName:'',
             passWord:'',
             userDetails:[],
             object:{
                userName:'',
                passWord:''
            }

        }
    }

    handleEvent=event=>{
        this.state.object.setState({
           userName: this.state.userName,
           passWord:this.state.passWord

        })
        this.state.userDetails.push(this.state.object);
        alert(`Welcome ${this.state.userName} `)
        console.log(this.state);
		event.preventDefault()

    }
    changeEventUserName=event=>{
        this.setState({
           userName:event.target.value
        })
    }
    changeEventPassWord=event=>{
            this.setState({
              passWord:event.target.value
            })

    }

    render() {

        const { userName, passWord } = this.state
        return (
        <div className="loginPage">
            <form>
                <h1 id="title">InstaTwitter</h1>
                <img src={require('./logo.gif')}  />
                <div id="textLogin">Login</div><br></br>
                <input 
                    type="text" 
                    id="username" 
                    placeholder="Enter username or phone number"
                    value={userName}
                    name={userName}
                    onChange={this.changeEventUserName}>
                </input><br></br>
                <br></br>
                <input 
                    type="password" 
                    id="password" 
                    placeholder="Enter Password" 
                    value={passWord}
                    name={passWord}
                    onChange={this.changeEventPassWord}>
                </input><br></br>
                <br></br><br></br>
                <button id="btnLogIn" type="button" onClick={this.handleEvent} >Log In</button>
            </form>   

        </div>
        )
    }
}

export default LoginPage
