import React, { Component } from 'react'
import './RegistrationPage.css'
import history from './history';


const validEmailRegex = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);

const validateForm = (errors) => {
    let valid = true;
    Object.values(errors).forEach(
      (val) => val.length > 0 && (valid = false)
    );
    return valid;
  }


export class RegistrationPage extends Component {
    constructor(props) {
        super(props)

        this.state = {
             username:'',
             password:'',
             email:'',
             phone:'',
             firstname:'',
             lastname:'',
             dob:'',
             userAccount:[],
             errors: {
                firstname: '',
                lastname:'',
                phone:'',
                dob:'',
                username:'',
                email: '',
                password: '',
              }
        }
    }
    clickEvent=event=>{

        event.preventDefault();
        if(validateForm(this.state.errors)) {
          console.info('Valid Form')
          alert(`Hey ${this.state.firstname}.... Registration Successful! `);
            this.state.userAccount.push(this.state);
            console.log(this.state.userAccount);
            history.push('/LoginPage')
        }else{
          console.error('Invalid Form')
        }

    }

    onChangeEvent=event=>{

        event.preventDefault();
    const { name, value } = event.target;
    let errors = this.state.errors;

    switch (name) {
      case 'firstname': 
        errors.firstname = 
          value.length < 2
            ? 'First Name must be 2 characters long!'
            : '';
        break;

      case 'lastname': 
        errors.lastname = 
          value.length < 2
            ? 'Last Name must be 2 characters long!'
            : '';
        break;
      case 'phone': 
        errors.phone = 
          value.length !== 10 
            ? 'Number should be of 10 digits!'
            : '';
        break;
      case 'dob': 
        errors.dob = 
          value.length !== 10 
            ? 'dob should be in dd-mm-yyyy format!'
            : '';
        break;
      case 'email': 
        errors.email = 
          validEmailRegex.test(value)
            ? ''
            : 'Email is not valid!';
        break;
      case 'username': 
        errors.username = 
          value.length < 5
            ? 'First Name must be 5 characters long!'
            : '';
        break;
      case 'password': 
        errors.password = 
          value.length < 8
            ? 'Password must be 8 characters long!'
            : '';
        break;
      default:
        break;
    }

    this.setState({errors, [name]: value});





       /* this.setState({
            [event.target.name]:event.target.value
        });*/


    }


    render() {
        const{username,password,email,phone,dob,firstname,lastname }=this.state
        const {errors} = this.state;
        return (
            <div className='reg'> 
            <h1 id="title">InstaTwitter</h1>
                <img src="./logo.png"></img>
            <div className="box">
            <form>

                <div className="registrationPage"><br></br>
                    <div id="textRegister">Registration</div>
                    <h3 id="tagline">Create your account here!</h3>
                    <div>
                    <label> Fisrt Name</label>
                        <input
                        type="text" 
                        id="firstname"
                        placeholder="Enter First Name" 
                        name="firstname"
                        value={firstname}
                        required=""
                        onChange={this.onChangeEvent}>
                        </input>
                        {errors.firstname.length > 0 && 
                        <span className='error'>{errors.firstname}</span>}

                    </div>
                    <div>
                    <label>Last Name</label>
                        <input type="text" 
                            id="lastname" 
                            placeholder="Enter Last Name"
                            name="lastname"
                            value={lastname}
                            required=""
                            onChange={this.onChangeEvent}>
                        </input>
                        {errors.lastname.length > 0 && 
                        <span className='error'>{errors.lastname}</span>}
                    </div>
                    <div>
                        <label>Email</label>
                        <input type="email" 
                            id="email" 
                            placeholder="Enter Email ID "
                            name="email"
                            value={email}
                            required=""
                            onChange={this.onChangeEvent}>
                        </input>
                        {errors.email.length > 0 && 
                        <span className='error'>{errors.email}</span>}
                    </div>
                    <div>
                        <label>Phone Number</label>
                        <input type="text" 
                            id="phone" 
                            placeholder="Enter Phone Number "
                            name="phone"
                            value={phone}
                            required=""
                            onChange={this.onChangeEvent}>
                        </input>
                        {errors.phone.length > 0 && 
                        <span className='error'>{errors.phone}</span>}
                    </div>
                    <div>
                        <label>Date of Birth</label>
                        <input type="text" 
                            id="dob" 
                            placeholder="Enter Date of Birth in dd-mm-yyyy format" 
                            name="dob"
                            value={dob}
                            required=""
                            onChange={this.onChangeEvent}>
                        </input>
                        {errors.dob.length > 0 && 
                        <span className='error'>{errors.dob}</span>}
                    </div>
                    <div>
                        <label>UserName</label>
                        <input type="text" 
                            id="use-rname" 
                            placeholder="Enter username "
                            name="username"
                            value={username}
                            required=""
                            onChange={this.onChangeEvent}>
                        </input>
                        {errors.username.length > 0 && 
                        <span className='error'>{errors.username}</span>}
                    </div>
                    <div>
                        <label>Password</label>
                        <input type="password" 
                            id="pass-word" 
                            placeholder="Set Password of 8 Characters" 
                            name="password"
                            value={password}
                            required=""
                            onChange={this.onChangeEvent}>
                        </input>

                        {errors.password.length > 0 && 
                        <span className='error'>{errors.password}</span>}
                    </div>



                    <button id="btnSignIn" type="Submit" onClick={this.clickEvent} >Register</button>


                 </div>

                </form>
                </div>  
            </div>
        )
    }
}

export default RegistrationPage
